(2, 'verify-mail', '{customer_name}, {code}', 'Verify Mail', '<tbody style=\"text-align: center;\"><tr><td style=\"padding-top: 0;\"><div style=\"background: #fff;box-shadow: 0 3px 4px #ddd;border-bottom-left-radius: 20px;border-bottom-right-radius: 20px;padding: 15px 40px 30px;\"><b style=\"margin-bottom: 10px; display: block;\">Hi {customer_name},</b><p>You can also verify manually by entering the following OTP</p><div style=\"padding:10px;border: 2px dashed #cb202d;word-break:keep-all!important;width: calc(100% - 40px);margin: 25px auto;\"><p style=\"Margin:0;Margin-bottom:16px;color:#cb202d;font-family:-apple-system,Helvetica,Arial,sans-serif;font-size:20px;font-weight:600;line-height:1.5;margin:0;margin-bottom:0;padding:0;text-align:center;word-break:keep-all!important\">{code}</p></div><p>Note: The OTP will expire in 10 minutes and can only be used once.</p></div></td></tr></tbody>', 'Verify Mail', '2021-12-17 14:08:42', '2021-12-17 14:08:42'),
(3, 'forgot-password', '{reset_link}', 'Forgot Password', '<tbody><tr><td><table style=\"background-color: #f2f3f8; max-width:670px; margin:0 auto;\" width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\"><tr><td><table width=\"95%\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"max-width:670px;background:#fff; border-radius:3px; text-align:center;-webkit-box-shadow:0 6px 18px 0 rgba(0,0,0,.06);-moz-box-shadow:0 6px 18px 0 rgba(0,0,0,.06);box-shadow:0 6px 18px 0 rgba(0,0,0,.06);\"><tr><td style=\"height:20px;\">&nbsp;</td></tr><tr><td style=\"padding:0 35px;\"> <h1 style=\"color:rgb(51,51,51);font-weight:500;line-height:27px;font-size:21px\">You have requested to reset your password</h1><span style=\"display:inline-block; vertical-align:middle; margin:29px 0 26px; border-bottom:1px solid #cecece; width:100px;\"></span><p style=\"color:#455056; font-size:15px;line-height:24px; margin:0;\"> We cannot simply send you your old password. A unique link to reset your password has been generated for you. To reset your password, click the following link and follow the instructions. </p> <a href=\"{reset_link}\" style=\"display: inline-block; padding: 6.7px 29px;border-radius: 4px;background:#8142ff;line-height: 20px; text-transform: uppercase;font-size: 14px;font-weight: 700;text-decoration: none;color: #fff;margin-top: 35px;\">Reset Password</a></td></tr><tr><td style=\"height:20px;\">&nbsp;</td></tr></table></td><tr><td style=\"height:20px;\">&nbsp;</td></tr></table></td></tr></tbody>', 'Reset Password Notification', '2021-12-17 14:08:42', '2021-12-17 14:08:42'),
(4, 'refund', '{product_image}, {product_name}, {price}', 'Refund', '<tbody><tr><td><table style=\"width:100%;border: 1px solid rgb(221 221 221 / 41%);\"> <thead> <tr> <th style=\"border-bottom: 1px solid rgb(221 221 221 / 41%);\"><h3 style=\"color:rgb(51,51,51);font-weight:bold;line-height:27px;font-size:21px\">Refund Confirmation</h3> </th> </tr> </thead> <tbody> <tr><td><b><span style=\"font-size:16px;line-height:21px\"> Hello Share, </span> </b> <p style=\"margin:1px 0px 8px 0px;font-size:14px;line-height:18px;color:rgb(17,17,17)\"> Lorem ipsum dolor sit amet consectetur, adipisicing elit. Totam sed vitae fugiat nam, ut natus officia optio a suscipit molestiae earum magni, voluptatum debitis repellat magnam. Officiis odit qui, provident doloremque dicta modi voluptatum placeat. </p></td></tr><tr><td><p style=\"margin:1px 0px 8px 0px;font-size:14px;line-height:18px;color:rgb(17,17,17)\"> You can find the list of possible reasons why the package is being returned to us as undelivered <a href=\"#\"><span style=\"color:#0066c0\">here</span></a>. If you still want the item, please check your address and place a new order. </p> </td> </tr> <tr> <td> <a style=\"display: inline-block; padding: 6.7px 29px;border-radius: 4px;background:#8142ff;line-height: 20px; text-transform: uppercase;font-size: 14px;font-weight: 700;text-decoration: none;color: #fff;\" href=\"#\"> View return &amp; refund status </a> </td> </tr> <tr> <td> <div style=\"padding: 10px;border: 1px solid rgb(221 221 221 / 41%);margin-top: 15px;\"> <ul style=\"display: flex;align-items: center;\"> <li style=\"width: 80px;height: 80px;margin-right: 30px;\"> <img src=\"{product_image}\" alt=\"\" style=\"width: 100%;height: 100%;object-fit: cover;border-radius: 4px;\"> </li> <li> <a href=\"#\"><b>{product_name}</b></a> </li> </ul> <hr style=\"border:0; border-bottom: 1px solid rgb(221 221 221 / 41%);margin: 15px 0 20px;\"> <p align=\"right\" style=\"margin:1px 0px 8px 0px;font-size:14px;line-height:18px;font-family:&quot;Roboto&quot;,&quot;Helvetica Neue&quot;,Helvetica,Arial,sans-serif;color:rgb(17,17,17)\"><b> <span style=\"font-size:16px\"> Refund total: <span style=\"font-size:16px\">${price}* </span> </span> </b><br> <span style=\"display:inline-block;text-align:left\"> Refund of ${price} is now initiated. </span> </p></div></td> </tr> <tr> <td> <table id=\"m_-2085618623145965177legalCopy\" style=\"margin:0px 0px 0px 0px;font-weight:400;font-style:normal;font-size:13px;color:rgb(170,170,170);line-height:16px\"> <tbody> <tr> <td><p style=\"font-size:13px;color:rgb(102,102,102);line-height:16px;margin:0\"> * Learn more <a href=\"#\"><span style=\"color:#0066c0\">about refunds</span></a> </p></td> </tr> <tr> <td><p style=\"font-size:13px;color:rgb(102,102,102);line-height:16px;margin:0\"> This email was sent from a notification-only address that cannot accept incoming email. Please do not reply to this message. </p></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody>', 'Refund', '2021-12-17 14:08:42', '2021-12-17 14:08:42'),
(5, 'orders', '{customer_name}, {description}, {products}, {order_id}, {address}', 'Orders', '<table style=\"width: 100%;background-color: #fff;padding: 50px 0 0;\">\n                <thead>\n                    <tr>\n                       <th colspan=\"2\" style=\"text-align: center;\">\n                         <h1 style=\"color: rgba(0,0,0,0.66);font-family: &quot;Times New Roman&quot;;font-size: 28px;font-weight: bold;letter-spacing: 0;line-height: 32px;\">Thanks for your order</h1>\n                         <p style=\"color: rgba(0,0,0,0.66);font-size: 15px;letter-spacing: 0;line-height: 25px;width: 80%;margin: 30px auto 10px;\"><span style=\"display: block;\">Hi {customer_name},</span> we have received your order and we working on it now.\n                          We will email you an update as soon as your order is processed.</p>\n                          </th>\n                       </tr>\n                 </thead>\n                <tbody>\n                    <tr>\n                        <td colspan=\"2\" style=\"padding-left: 0;padding-right: 0;\">\n                            <table style=\"width:100%; border: 1px solid rgb(221 221 221 / 41%);\">\n                                <tbody>\n                                      <tr>\n                                        <td colspan=\"2\" style=\"padding: 0;\">\n                                            <table style=\"width:100%;\">\n                                                <tbody> {products} </tbody>\n                                            </table>\n                                        </td>\n                                    </tr>\n                                </tbody>\n                            </table>\n                        </td>\n                    </tr>\n                </tbody>\n            </table>', 'Orders', '2021-12-17 14:08:42', '2021-12-17 14:08:42'),
